from datetime import datetime, date, timeimport osimport pandas as pdclass APP:    def __init__(self,lists,host):        self.lists = lists        self.host = host        self.app_en()    def app_en (self):        rezult =[]        for d in self.lists:            if len(d.split('/')) >= 6:                continue            elif d in rezult:                continue            elif (f'{self.host}{d}') in rezult:                continue            else:                rezult.append(f'{self.host}{d}')        return rezultdt = datetime.strftime(datetime.now(), "%Y.%m.%d %H:%M")def now_folder():    pa_th = str(os.path.abspath('{}'.format(dt[:-6])))    if os.path.exists('{}'.format(dt[:-6])):        print('фаил существует',type(pa_th))    else:        os.mkdir('{}'.format(dt[:-6]))        print('фаил создан',pa_th)    return pa_thdef un_pak (list):    df = pd.DataFrame(({'name': [], 'praise': [], 'vale': []}))    for i in range(len(list)):        i = (list[i])[0]        df.loc[i[0]]=[i[1],i[2],i[3]]    path = now_folder() + '\\'    dt_min = datetime.strftime(datetime.now(), "%H_%M")    df.to_excel(path+'{}.xlsx'.format(dt_min))